widgets.onValue:
var fallTime = 0.2,
    fps = 24,
    maxValue = 1,
    step = maxValue / fps / fallTime

setTimeout(()=>{
  if (value <= 0.2) clearTimeout()
  else set("this", value - step)
}, 1000 / fps)

var fallTime = 0.2, fps = 8, maxValue = 1
var step = maxValue / fps / fallTime
setTimeout(()=>{
  if (value <= -20) clearTimeout()
  else set('this', value - step)
}, 1000 / fps)
